contracts/MockERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract MockERC20 is ERC20 {
    uint8 private _decimals;

    constructor(
        string memory name,
        string memory symbol,
        uint256 initialSupply
    ) ERC20(name, symbol) {
        _decimals = 18; // Default decimals
        _mint(msg.sender, initialSupply);
    }

    // Override decimals function
    function decimals() public view override returns (uint8) {
        return _decimals;
    }

    // Fungsi mint untuk menambahkan token ke alamat tertentu
    function mint(address to, uint256 amount) public {
        _mint(to, amount);
    }
}

contracts/TokenTransfer.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

contract TokenTransfer {
    event TransferExecuted(address indexed from, address indexed to, uint256 amount);

    function transferToken(
        address token,
        address to,
        uint256 amount
    ) external {
        require(to != address(0), "Invalid recipient");

        bool success = IERC20(token).transferFrom(msg.sender, to, amount);
        require(success, "Token transfer failed");

        emit TransferExecuted(msg.sender, to, amount); // Emit event saat transfer berhasil
    }
}

interface IERC20 {
    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external returns (bool);
}

